import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import HeadBar from './appbar';
import UploadCard from './upload';
import ResultCard from './result';
import withRoot from '../withRoot';

const styles = theme => ({
    root:{
        flexGrow: 1,
        backgroundColor: theme.palette.secondary.light,
        height: '100vh',
        width: '100vw',
    },
    uploadcard: {
        marginTop: '2rem',
    },
    resultcard: {
        marginTop: '2rem',
    }
});


class Page extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            dataFromChild: null,
            expanded: false
        };
    }

    handleData = (data) => {
        this.setState({ dataFromChild: data});
        console.log(this.state.dataFromChild)
    };

    render () {
        const { classes } = this.props;

        return (
            <div className={ classes.root }>
                <HeadBar/>
                <UploadCard className={ classes.uploadcard } passData={this.handleData}/>
                <ResultCard className={ classes.resultcard}/>
            </div>
        )
    }
}

Page.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withRoot(withStyles(styles)(Page));
