import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import Input from '@material-ui/core/Input'
import Button from '@material-ui/core/Button';
import withRoot from '../withRoot';


const styles = theme => ({
    root: {
        margin: '2rem',
        marginTop: '5rem',
        padding: '1rem',
    },
    title: {
        color: theme.palette.primary.dark
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        })
    },
    expandOpen: {
        transform: 'rotate(180deg)'
    },
    form: {
        marginLeft: '2rem',
        width: '50%'
    },
    text:{
        width: '80%',
        marginLeft: '-2rem',

    },
    submit:{
        marginTop: '2rem',
    },
    cancel:{
        width: '10rem',
    },
    submitbtn: {
        width: '10rem',
        backgroundColor: theme.palette.primary.main,

        '&:hover': {
            backgroundColor: theme.palette.primary.dark
        }

    }

});




class UploadCard extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            expanded: true,
            selectedFile: null,
        };
    }


    handleExpandClick = () => {
        this.setState(state => ({ expanded: !state.expanded }))
    };


    handleCancel = () => {
        document.getElementById('file').value = ""
    };

    handleSubmit = (ev) => {
        ev.preventDefault();
        // Imports the Google Cloud client library
        const speech = require('@google-cloud/speech');
        const fs = require('fs');

        // Creates a client
        const client = new speech.SpeechClient();

        // The name of the audio file to transcribe
        const fileName = this.state.selectedFile;

        // Reads a local audio file and converts it to base64
        const file = fs.readFileSync(fileName);
        const audioBytes = file.toString('base64');

        // The audio file's encoding, sample rate in hertz, and BCP-47 language code
        const audio = {
            content: audioBytes,
        };
        const config = {
            encoding: 'LINEAR16',
            sampleRateHertz: 16000,
            languageCode: 'en-US',
        };
        const request = {
            audio: audio,
            config: config,
        };

        // Detects speech in the audio file
        client
            .recognize(request)
            .then(data => {
                const response = data[0];
                const transcription = response.results
                    .map(result => result.alternatives[0].transcript)
                    .join('\n');
                console.log(`Transcription: ${transcription}`);
            })
            .catch(err => {
                console.error('ERROR:', err);
            });
    };

    onChangeHandler = ev => {
        this.setState({selectedFile: ev.target.files[0]})
    };

    render() {
        const { classes } = this.props;

        return (
            <Card className={ classes.root }>
                <CardHeader classes = {{ title: classes.title,}}
                    title={ "1. Upload audio files" }
                    action={
                        <IconButton className={classnames(classes.expand, {
                            [classes.expandOpen]: this.state.expanded,
                        })}
                        onClick = {this.handleExpandClick}
                        aria-expanded = {this.state.expanded}>
                            <ExpandMoreIcon />
                        </IconButton>
                    }
                />

                <Collapse in={this.state.expanded} timeout='auto' unmountOnExit>
                    <form className={ classes.form } onSubmit={this.handleSubmit} >
                        <Grid container spacing={0}>
                            <Grid item xs={12}>
                                <Input id='file' type='file' variant='outlined' onChange={this.onChangeHandler}>Browse</Input>
                            </Grid>
                        </Grid>
                        <Grid container spacing={0} className={ classes.submit} >
                            <Grid item xs={6}>
                                <Button variant='outlined' className={ classes.cancel } onClick={this.handleCancel}>Cancel</Button>
                            </Grid>
                            <Grid item xs={6}>
                                <Button type='submit' variant='contained' className={ classes.submitbtn }>Submit</Button>
                            </Grid>
                        </Grid>
                    </form>
                </Collapse>

            </Card>
        )
    }
}

UploadCard.propTypes = {
   classes: PropTypes.object.isRequired,
};

export default withRoot(withStyles(styles)(UploadCard));
