import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import classnames from 'classnames';
import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import withRoot from '../withRoot';


const styles = theme => ({
    root: {
        position: 'fixed',
        width: '100vh',
        margin: '2rem',
        marginTop: '20rem',
        padding: '1rem',
    },
    title: {
        color: theme.palette.primary.dark
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        })
    },
    expandOpen: {
        transform: 'rotate(180deg)'
    },
    speaker: {
        color: theme.palette.primary.main,
    }
});

class ResultCard extends React.Component{
    constructor(prop){
        super(prop);

        this.state = {
            expanded: false
        }
    }

    handleExpandClick = () => {
        this.setState(state => ({ expanded: !state.expanded }))
    };


        render(){
        const { classes } = this.props;

        return (
            <Card className={ classes.root }>
                <CardHeader classes = {{ title: classes.title,}}
                            title={ "2. See transcripts" }
                            action={
                                <IconButton className={classnames(classes.expand, {
                                    [classes.expandOpen]: this.state.expanded,
                                })}
                                            onClick = {this.handleExpandClick}
                                            aria-expanded = {this.state.expanded}>
                                    <ExpandMoreIcon />
                                </IconButton>
                            }
                />

                <Collapse in={this.state.expanded} timeout='auto' unmountOnExit>
                    <CardContent>
                        <List>
                            <ListItem>
                                <Grid container spacing={8}>
                                    <Grid item xs={3}>
                                        <ListItemText className={ classes.speaker }>123</ListItemText>
                                    </Grid>
                                    <Grid item xs={9} className={ classes._transcript }>
                                        <ListItemText>123321</ListItemText>
                                    </Grid>
                                </Grid>
                                </ListItem>
                        </List>
                    </CardContent>
                </Collapse>
            </Card>
        )
    }

}

ResultCard.protoTypes = {
    classes: PropTypes.object.isRequired,
};

export default withRoot(withStyles(styles)(ResultCard))
