import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import classnames from 'classnames';
import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import withRoot from '../withRoot';

import {connect} from 'react-redux';
import { seekUri, getResult } from '../redux/actions';

import compose from 'recompose/compose';
import store from '../redux/store';


const styles = theme => ({
    root: {
        margin: '2rem',
        marginTop: '3rem',
        padding: '1rem',
    },
    title: {
        color: theme.palette.primary.dark
    },
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
            duration: theme.transitions.duration.shortest,
        }),
    },
    expandOpen: {
        transform: 'rotate(180deg)'
    },
    speaker: {
        color: theme.palette.primary.dark,
        fontSize: 16,
        fontWeight: 500
    }
});

const mapStateToProps = state => ({
    progress: state.changeProgress.progress
});

function seekResult(name){
    fetch('http://localhost:8000/seekresult', {
        method: 'POST',
        mode:'cors',
        cache: 'no-cache',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({name: name})
    }).then(res => {
        return res.json()
    }).then(data => {
        console.log(data);
        getResultFile(data.uri)
    }).catch(err => {
        console.log(err)
    })
}

function getResultFile(uri){
    fetch(uri, {
        method: 'GET',
        mode: 'cors',
        cache: 'no-cache'
    }).then(res => {
            return res.json()
        }).then(data => {
            console.log(data.results.transcripts);
            this.setState({result: data});
            store.dispatch(getResult())
        }).catch(err => {
            console.log(err)
        })
}


class ResultCard extends React.Component{
    constructor(prop){
        super(prop);

        this.state = {
            expanded: false,
            name: this.props.name,
            result: null,
        }
    }

    handleExpandClick = () => {
        this.setState(state => ({ expanded: !state.expanded }));
    };


    render(){
    const { classes } = this.props;

    if (this.props.progress === 'uploaded') {
        console.log(this.props.name);
        seekResult(this.props.name);
        store.dispatch(seekUri())
    }

    return (
        <Card className={ classes.root }>
            <CardHeader classes = {{ title: classes.title,}}
                        title={ "2. See transcripts" }
                        action={
                            <IconButton disabled={true} className={classnames(classes.expand, {
                                [classes.expandOpen]: this.state.expanded,
                            })}
                                        onClick = {this.handleExpandClick}
                                        aria-expanded = {this.state.expanded}>
                                <ExpandMoreIcon />
                            </IconButton>
                        }
            />

            <Collapse in={this.state.expanded} timeout='auto' unmountOnExit>
                <CardContent>
                    <List>
                        <ListItem>
                            <Grid container spacing={8}>
                                {this.props}
                                <Grid item xs={3}>
                                    <ListItemText>
                                        <Typography className={ classes.speaker }>Speaker 1:</Typography>
                                    </ListItemText>
                                </Grid>
                                <Grid item xs={9} className={ classes._transcript }>

                                    <ListItemText>123321</ListItemText>
                                </Grid>
                            </Grid>
                            </ListItem>
                    </List>
                </CardContent>
            </Collapse>
        </Card>
    )
}

}

ResultCard.protoTypes = {
    classes: PropTypes.object.isRequired,
};

export default withRoot(compose(
    withStyles(styles, {name: 'ResultCard'}),
    connect(mapStateToProps)
)(ResultCard));
